// Function to download data to a file
function download(data, filename, type) {

	var file = new Blob([data], { type: type });
	if (window.navigator.msSaveOrOpenBlob) // IE10+
		window.navigator.msSaveOrOpenBlob(file, filename);
	else { // Others
		var a = document.createElement("a"),
			url = URL.createObjectURL(file);
		a.href = url;
		a.download = filename;
		document.body.appendChild(a);
		a.click();
		setTimeout(function () {
			document.body.removeChild(a);
			window.URL.revokeObjectURL(url);
		}, 0);
	}
}

symbaroum.filter('rechercheFilter', function () {

	return function (items, query) {

		var filtered = [];
		
		if ((!(query.nom === "") && !(query.nom === undefined)) || query.epingleSeulement || !(query.filtreType === "tous" && query.filtreLivre === "tous" && query.filtreTrad === "tous" && query.filtreAttr === "tous")) {

			filtered = items.filter(item => {
				
				if (item.epingle || item.epinglen || item.epinglea || item.epinglem) {

					return true;
				} else if (!query.epingleSeulement) {
					var found = false;
					var tokens = query.nom.toLowerCase().split(",");
					tokens.forEach(token => {
						
						token = token.trim();
						if (undefined != item[query.lang] && (item[query.lang].nom.toLowerCase().includes(token) || token === "")
							&& ((item.type.split("/").includes(query.filtreType)) || query.filtreType === "tous")
							&& ((query.filtreLivre === item.livre) || query.filtreLivre === "tous")
							&& ((item[query.lang].tradition && item[query.lang].tradition.toLowerCase().includes(query.filtreTrad)) || query.filtreTrad === "tous" || (query.filtreTrad === "aucune" && !item[query.lang].tradition))
							&& ((item.attribut && item.attribut.split(",").includes(query.filtreAttr)) || query.filtreAttr === "tous")) {

								found = true;
						}
					});
					return found;
				}
			})
		} else {
			return items;
		}

		return filtered;
	};
});

symbaroum.directive('onReadFile', function ($parse) {
	
	return {
		restrict: 'A',
		scope: false,
		link: function (scope, element, attrs) {
			var fn = $parse(attrs.onReadFile);

			element.on('change', function (onChangeEvent) {
				var reader = new FileReader();

				reader.onload = function (onLoadEvent) {
					scope.$apply(function () {
						fn(scope, { $fileContent: onLoadEvent.target.result });
					});
				};

				reader.readAsText((onChangeEvent.srcElement || onChangeEvent.target).files[0]);
			});
		}
	};
});

symbaroum.factory('globalService', function () {

	return {
		encoding: function (data, route) {

			var compressed = window.btoa(escape(LZString.compress(JSON.stringify(data))));
			return window.location.origin + window.location.pathname
				+ "#!/" + route + "?b64=" + compressed;
		},
		decoding: function (data) {

			var uncompressed = LZString.decompress(unescape(window.atob(data)));
			return uncompressed;
		}
	};
});

