routeAppControllers.controller('LostPwd', function ($scope, $translate, $http) {

	$scope.pwdReset = () => {

		$('form[name="pwdResetForm"]').addClass("was-validated");

		if (!$scope.pwdResetForm.$valid) {
			return;
		}

		$http.post('https://symbatools-api.tk/users/pwdReset', { "username": $scope.email }).then(
			function(response) {
				
				$scope.emailSent = true;
				$scope.message =  $translate.instant('MSG_RES_EMAIL_SENT');
				$('#staticBackdrop').modal('show');
			},
			function(response) {
				
				$scope.message = $translate.instant(response.data.error);
				$('#staticBackdrop').modal('show');
			}
		);

		$('form[name="pwdResetForm"]').removeClass("was-validated");
	}

	$scope.validateToken = () => {

		$('form[name="tokenForm"]').addClass("was-validated");
		
		if (!$scope.tokenForm.$valid) {
			return;
		}

		$http.post('https://symbatools-api.tk/users/pwdReset/validation/' + $scope.token, { "username": $scope.email }).then(
			function(response) {
				
				$scope.tokenValidated = true;
				$scope.message =  $translate.instant('MSG_RES_TOKEN_OK');
				$('#staticBackdrop').modal('show');
			},
			function(response) {

				$scope.message = $translate.instant(response.data.error);
				$('#staticBackdrop').modal('show');
			}
		);

		$('form[name="pwdResetForm"]').removeClass("was-validated");
	}

	$scope.changePwd = () => {

		$('form[name="pwdChangeForm"]').addClass("was-validated");

		if ($scope.pwd1 !== $scope.pwd2) {

			$scope.pwdChangeForm.pwd2.$setValidity('pwd', false);
		}

		if (!$scope.pwdChangeForm.$valid) {
			return;
		}

		$http.post('https://symbatools-api.tk/users/pwdReset/changePwd', { "username": $scope.email, "newPassword": $scope.pwd1, "token": $scope.token }).then(
			function(response) {
				$('#cardBodyPwdChange').text($scope.message =  $translate.instant('MSG_RES_PWD_CHANGED'));
			},
			function(response) {

				$scope.message = $translate.instant(response.data.error);
				$('#staticBackdrop').modal('show');
			}
		);
	}
})

routeAppControllers.controller('Test', function ($scope, $q, $http) {

	$scope.jsonAllLang = {};

	$http({

		method: 'GET',
		url: window.location.origin + '/json/fr_new.json'
	}).then(function successCallback(responseFr) {

		
		$http({

			method: 'GET',
			url: window.location.origin + '/json/es_new.json'
		}).then(function successCallback(responseEs) {
	
			$http({

				method: 'GET',
				url: window.location.origin + '/json/en_new.json'
			}).then(function successCallback(responseEn) {
		
				Object.keys(responseEn.data).forEach(key => {

					$scope.jsonAllLang[key] = {};
					$scope.jsonAllLang[key].type = responseEn.data[key].type;
					$scope.jsonAllLang[key].livre = responseEn.data[key].livre;
					$scope.jsonAllLang[key].attribut = responseEn.data[key].attribut;

					var tmpFr = responseFr.data[key];
					if (undefined != tmpFr) {

						delete tmpFr.type;
						delete tmpFr.livre;
						delete tmpFr.attribut;
						$scope.jsonAllLang[key].fr = tmpFr;
					}

					var tmpEs = responseEs.data[key];
					if (tmpEs != null) {

						delete tmpEs.type;
						delete tmpEs.livre;
						delete tmpEs.attribut;
						$scope.jsonAllLang[key].es = tmpEs;
					}
					
					var tmpEn = responseEn.data[key];
					delete tmpEn.type;
					delete tmpEn.livre;
					delete tmpEn.attribut;
					$scope.jsonAllLang[key].en = tmpEn;
				});
			}, function errorCallback(response) {
		
				console.log(response);
			});
		}, function errorCallback(response) {
	
			console.log(response);
		});
	}, function errorCallback(response) {

		console.log(response);
	});
})

